    private boolean exists(Connection conn, String tableName) {
        if (conn == null)
            return false;

        DatabaseMetaData md = null;
        ResultSet rs = null;

        try {
            md = conn.getMetaData();
            rs = md.getTables(null, null, "%", null);
            while (rs.next()) {
                String name = rs.getString(3);              
                if (name.equalsIgnoreCase(tableName))                     
